function initMap() {
    var e = { lat: 51.501364, lng: -0.141890 }, // Buckingham Palace coordinates
        t = new google.maps.Map(document.getElementById("map-canvas"), {
            zoom: 15,
            center: e,
            scrollwheel: !1
        });
    new google.maps.Marker({
        position: e,
        map: t
    });
}
